import React from 'react'
// import { Link } from 'react-router-dom'
import { auth } from '../../firebase/firebase.utils'
import { useSelector } from 'react-redux'

// import './header.styles.scss'
import { HeaderContainer, LogoContainer, OptionsContainer, OptionDiv, OptionLink } from './header.styles'
import { ReactComponent as Logo } from '../../assests/crown.svg'
import CartIcon from '../cart-icon/cart-icon'
import CartDropdown from '../cart-dropdown/cart-dropdown'

const Header = () => {
    // useSelector as you would mapStateToProps in class based components
    // `state` is from our store.js `user` is from root-reducer.js which combines all reducers
    // `currentUser` is the state we created in user-reducer.js
    const currentUser = useSelector(state => state.user.currentUser)
    const hidden = useSelector(state => state.cart.hidden)
    return (
        <HeaderContainer>
            <LogoContainer to='/'>
                <Logo className='logo' />
            </LogoContainer>
            <OptionsContainer>
                <OptionLink to='/shop'>
                    SHOP
                </OptionLink>
                <OptionLink to='/shop'>
                    CONTACT
                </OptionLink>
                {
                    currentUser ? (<OptionDiv onClick={() => auth.signOut()}>SIGN OUT</OptionDiv>) : (<OptionLink to='/signin'>SIGN IN</OptionLink>)
                }
                <CartIcon />
            </OptionsContainer>
            {hidden ? null : <CartDropdown />}
        </HeaderContainer>
    )
}

// const mapStateToProps = ({user: {currentUser}, cart: {hidden}}) => ({
//     currentUser,
//     hidden
// })
// const mapStateToProps = createStructuredSelector({
//     currentUser: selectCurrentUser,
//     hidden: selectCartHidden,
// })

// export default connect(mapStateToProps)(Header)

export default Header
