import React, { useEffect } from 'react';
import { Route, Switch } from 'react-router-dom'
import { connect, useDispatch, useSelector } from 'react-redux'
import { auth, createUserProfileDocument } from './firebase/firebase.utils'
import { setCurrentUser } from './redux/user/user-actions'
import './App.css';

import HomePage from './components/pages/homepage/homepage'
import ShopPage from './components/pages/shop/shop'
import Header from './components/header/header.jsx'
import SignInUp from './components/pages/shop/sign-in-up/sign-in-up'

const App = () => {
  // const [currentUser, setCurrentUser] = useState(null)
  // let unsubscribeFromAuth = useRef(null)
  // const user = useSelector(state => state.user.currentUser)
  const dispatch = useDispatch()
  // const user = { name: "Rei" }

  dispatch(setCurrentUser(user))
  useEffect(() => {
    // unsubscribeFromAuth.current =
    auth.onAuthStateChanged(async userAuth => {
      console.log('this is userAuth')
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth)
        userRef.onSnapshot(snapShot => {
          console.log('this is the snapshot ', snapShot.data())
          setCurrentUser({
            id: snapShot.id,
            ...snapShot.data(),
          })
        })
      }
      // else {
      //   setCurrentUser(null)
      // }
    })

  }, [])


  return (
    <div>
      <Header />
      <Switch>
        <Route exact path='/' component={HomePage} />
        <Route path='/shop' component={ShopPage} />
        <Route path='/signin' component={SignInUp} />
      </Switch>
      {
        user.loggedIn ?
          <>
            <h1>Hello, {user.name}</h1>
            <button onClick={() => dispatch(setCurrentUser(null))}>Logout</button>
          </>
          :
          <>
            <h1>Login</h1>
            <button onClick={() => dispatch(setCurrentUser(user))}>Login as Rei</button>
          </>
      }
    </div>
  )
}

